import random

def generate():
    width = random.randint(1, 30)
    height = random.randint(1, 30)
    input_grid = [[random.randint(0, 9) for _ in range(width)] for _ in range(height)]
    
    center_row = height // 2
    center_col = width // 2
    
    center_row_all_5 = all(input_grid[center_row][c] == 5 for c in range(width))
    center_col_all_5 = all(input_grid[r][center_col] == 5 for r in range(height))
    
    if center_row_all_5 and center_col_all_5:
        input_grid[center_row][0] = random.randint(0, 4)
    
    output_grid = [row[:] for row in input_grid]
    for c in range(width):
        output_grid[center_row][c] = 5
    for r in range(height):
        output_grid[r][center_col] = 5
    
    return {"input": input_grid, "output": output_grid}


