def p(input_grid):
    input_grid = [list(row) for row in input_grid]
    height = len(input_grid)
    width = len(input_grid[0])
    center_row = height // 2
    center_col = width // 2
    output = [row[:] for row in input_grid]
    for c in range(width):
        output[center_row][c] = 5
    for r in range(height):
        output[r][center_col] = 5
    return output
