import random
def generate():
    width = random.randint(2, 30)
    height = random.randint(2, 30)
    c1 = random.randint(0, 9)
    c2 = random.randint(0, 9)
    while c1 == c2:
        c2 = random.randint(0, 9)
    input_grid = [[c1 if (i + j) % 2 == 0 else c2 for j in range(width)] for i in range(height)]
    output_grid = [[c2 if (i + j) % 2 == 0 else c1 for j in range(width)] for i in range(height)]
