def p(input_grid):
    colors = set()
    for row in input_grid:
        for cell in row:
            colors.add(cell)
    c1, c2 = sorted(colors)
    output_grid = [[c2 if cell == c1 else c1 for cell in row] for row in input_grid]
    return output_grid
